    SpinnerComp
  },
  data() {
    return {
      loading: true,
    };
  },
  computed: {
    about() {
      return this.$store.state.about || [];
    }
  },
  async mounted() {
    try {
      await this.$store.dispatch('fetchAbout');
      this.loading = false;
    } catch (error) {
      console.error('Error fetching about data', error);
      this.loading = false;
    }
  },
};
</script>

